/**
 * Data definitions for spells.
 */

import { Background } from './backgrounds.js';
import { Book, BookReference } from './books.js';
import { Class, ClassDescription } from './classes.js';
import { Attribute, Condition, CreatureType, DamageType, Time, TimeUnit } from './general.js';
import { Race } from './races.js';
import { createEnum, createEnumFromMapping } from './utils.js';

/**
 * School of a spell.
 */
const School = createEnumFromMapping(new Map([['a', 'Abjuration'], ['v', 'Evocation'], ['e', 'Enchantment'], ['i', 'Illusion'], ['d', 'Divination'], ['n', 'Necromancy'], ['t', 'Transmutation'], ['c', 'Conjuration'], ['p', 'Psionic']]));

/**
 * Type (shape) of spell range.
 */
const RangeType = createEnum(['cone', 'cube', 'hemisphere', 'line', 'point', 'radius', 'sphere', 'special']);

/**
 * Type of distance.
 */
const DistanceType = createEnum(['feet', 'miles', 'self', 'sight', 'touch', 'unlimited']);

/**
 * Distance for spell casting.
 */
class Distance {
    /**
     * @param {DistanceType} distance_type
     * @param {int} amount
     */
    constructor ({ distance_type, amount = null }) {
        this.distance_type = distance_type;
        this.amount = amount;
    }
};

/**
 * Range of a spell.
 */
class Range {
    /**
     * @param {RangeType} range_type
     * @param {Distance} distance
     */
    constructor ({ range_type, distance = null }) {
        this.range_type = range_type;
        this.distance = distance;
    }
};

/**
 * Material component required for a spell.
 */
class MaterialComponent {
    /**
     * @param {String} text
     * @param {int} cost
     * @param {bool} consume
     */
    constructor ({ text, cost = 0, consume = false }) {
        this.text = text;
        this.cost = cost;
        this.consume = consume;
    }
};

/**
 * Components of a spell, with booleans or a subclass for uniformity.
 */
class SpellComponents {
    /**
     * @param {bool} verbal
     * @param {bool} somatic
     * @param {MaterialComponent} material
     * @param {bool} royalty
     */
    constructor ({ verbal = false, somatic = false, material = null, royalty = false }) {
        this.verbal = verbal;
        this.somatic = somatic;
        this.material = material;
        this.royalty = royalty;
    }
};

/**
 * Modality of spell duration.
 */
const DurationType = createEnum(['instant', 'permanent', 'special', 'timed']);

/**
 * Duration of spell effect.
 */
class SpellDuration {
    /**
     * @param {DurationType} duration_type
     * @param {Time} time
     * @param {bool} concentration
     * @param {String[]} ends
     * @param {String} condition
     */
    constructor ({ duration_type, time = null, concentration = false, ends = null, condition = null }) {
        this.duration_type = duration_type;
        this.time = time;
        this.concentration = concentration;
        this.ends = ends;
        this.condition = condition;
    }
};

/**
 * Attack types.
 */
const AttackType = createEnumFromMapping(new Map([['m', 'Melee'], ['r', 'Ranged'], ['o', 'Other/unknown']]));

/**
 * Damage inflicted by the spell.
 */
class Damage {
    /**
     * @param {DamageType[]} damage_types
     * @param {AttackType[]} attack_types
     */
    constructor ({ damage_types, attack_types }) {
        this.damage_types = damage_types;
        this.attack_types = attack_types;
    }
};

/**
 * Eldritch invocation.
 */
class EldritchInvocation {
    /**
     * @param {String} name_
     * @param {Book} source
     */
    constructor ({ name_, source }) {
        this.name_ = name_;
        this.source = source;
    }
};

/**
 * Domain representation of a spell.
 */
class Spell {
    /**
     * @param {String} name_
     * @param {BookReference[]} sources
     * @param {int} min_level
     * @param {School} school
     * @param {Time[]} cast_times
     * @param {Range} spell_range
     * @param {SpellComponents} components
     * @param {SpellDuration[]} durations
     * @param {Description} description
     * @param {String} higher_level_description
     * @param {Class[]} classes
     * @param {Race[]} races
     * @param {Background[]} backgrounds
     * @param {Condition[]} conditions_inflicted
     * @param {Condition[]} conditions_immune
     * @param {Attribute[]} saving_throws
     * @param {CreatureType[]} creatures_affected
     * @param {Damage} damage
     * @param {DamageType[]} damage_resist
     * @param {DamageType[]} damage_immune
     * @param {Attribute[]} ability_check
     * @param {String} srd_name
     * @param {EldritchInvocation[]} eldritch_invocations
     */
    constructor ({ name_, sources, min_level, school, cast_times, spell_range, components, durations, description, higher_level_description = null, classes = null, races = null, backgrounds = null, conditions_inflicted, conditions_immune, saving_throws, creatures_affected, damage, damage_resist, damage_immune, ability_check, srd_name = null, eldritch_invocations = null }) {
        this.name_ = name_;
        this.sources = sources;
        this.min_level = min_level;
        this.school = school;
        this.cast_times = cast_times;
        this.spell_range = spell_range;
        this.components = components;
        this.durations = durations;
        this.description = description;
        this.higher_level_description = higher_level_description;
        this.classes = classes;
        this.races = races;
        this.backgrounds = backgrounds;
        this.conditions_inflicted = conditions_inflicted;
        this.conditions_immune = conditions_immune;
        this.saving_throws = saving_throws;
        this.creatures_affected = creatures_affected;
        this.damage = damage;
        this.damage_resist = damage_resist;
        this.damage_immune = damage_immune;
        this.ability_check = ability_check;
        this.srd_name = srd_name;
        this.eldritch_invocations = eldritch_invocations;
    }
};

